mapper.select:
select 

from doc_auth
where 1 = 1

  and ID = #{id,jdbcType=INTEGER}


  and USER_ID = #{userId,jdbcType=INTEGER}


  and DOC_ID = #{docId,jdbcType=INTEGER}


  and REPOS_ID = #{reposId,jdbcType=INTEGER}


  and IS_ADMIN = #{isAdmin,jdbcType=INTEGER}



  and ACCESS = #{access,jdbcType=INTEGER}


  and EDIT_EN = #{editEn,jdbcType=INTEGER}


  and ADD_EN = #{addEn,jdbcType=INTEGER}


  and DELETE_EN = #{deleteEn,jdbcType=INTEGER}


  and HERITABLE = #{heritable,jdbcType=INTEGER},

select 

from doc_auth
where 1 = 1

  and ID = #{id,jdbcType=INTEGER}


  and (USER_ID = #{userId,jdbcType=INTEGER} or USER_ID = 0)


  and DOC_ID = #{docId,jdbcType=INTEGER}


  and REPOS_ID = #{reposId,jdbcType=INTEGER}


  and IS_ADMIN = #{isAdmin,jdbcType=INTEGER}



  and ACCESS = #{access,jdbcType=INTEGER}


  and EDIT_EN = #{editEn,jdbcType=INTEGER}


  and ADD_EN = #{addEn,jdbcType=INTEGER}


  and DELETE_EN = #{deleteEn,jdbcType=INTEGER}


  and HERITABLE = #{heritable,jdbcType=INTEGER},

mapper.delete:
delete from doc_auth
  where 1 = 1

    and ID = #{id,jdbcType=INTEGER},


    and USER_ID = #{userId,jdbcType=INTEGER}


    and DOC_ID = #{docId,jdbcType=INTEGER}


    and REPOS_ID = #{reposId,jdbcType=INTEGER}

mapper.select:
select 
	doc_auth.ID, USER_ID, DOC_ID, REPOS_ID, IS_ADMIN, ACCESS, EDIT_EN, ADD_EN, DELETE_EN, HERITABLE
from doc_auth 
LEFT JOIN doc on doc.ID = doc_auth.DOC_ID
where doc_auth.USER_ID = #{userId,jdbcType=INTEGER}

    and doc.ID = #{docId,jdbcType=INTEGER}


    and doc.PID = #{pDocId,jdbcType=INTEGER}


    and doc.VID = #{reposId,jdbcType=INTEGER}
