mapper.insert:
insert into repos (ID, NAME, TYPE, 
  PATH, SVN_PATH, SVN_USER, 
  SVN_PWD, INFO, MENU, 
  PWD, OWNER, CREATE_TIME
  )
values (#{id,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{type,jdbcType=INTEGER}, 
  #{path,jdbcType=VARCHAR}, #{svnPath,jdbcType=VARCHAR}, #{svnUser,jdbcType=VARCHAR}, 
  #{svnPwd,jdbcType=VARCHAR}, #{info,jdbcType=VARCHAR}, #{menu,jdbcType=VARCHAR}, 
  #{pwd,jdbcType=VARCHAR}, #{owner,jdbcType=INTEGER}, #{createTime,jdbcType=VARCHAR}
  )

mapper.select:
select 

from repos
where 1 = 1

  and ID = #{id,jdbcType=INTEGER}


  and NAME = #{name,jdbcType=VARCHAR}


  and TYPE = #{type,jdbcType=INTEGER}


  and PATH = #{path,jdbcType=VARCHAR}


  and SVN_PATH = #{svnPath,jdbcType=VARCHAR}


  and SVN_USER = #{svnUser,jdbcType=VARCHAR}


  and SVN_PWD = #{svnPwd,jdbcType=VARCHAR}


  and INFO = #{info,jdbcType=VARCHAR}


  and MENU = #{menu,jdbcType=VARCHAR}


  and PWD = #{pwd,jdbcType=VARCHAR}


  and OWNER = #{owner,jdbcType=INTEGER}


  and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}
