mapper.select:
    select 
    
    from doc
    where 1 = 1
    
        and ID = #{id,jdbcType=INTEGER}
    
    
    	and NAME = #{name,jdbcType=VARCHAR}
    
    
    	and TYPE = #{type,jdbcType=INTEGER}
    
    
        and SIZE = #{size,jdbcType=INTEGER}
    
    
        and CHECK_SUM = #{checkSum,jdbcType=VARCHAR}
    
    
        and REVISION = #{revision,jdbcType=VARCHAR}
    
    
        and PATH = #{path,jdbcType=VARCHAR}
    
    
    	and PID = #{pid,jdbcType=INTEGER}
    
    
        and VID = #{vid,jdbcType=INTEGER}
    
	
		and CREATOR = #{creator,jdbcType=INTEGER}
	
	
		and CREATE_TIME = #{createTime,jdbcType=BIGINT}
	
	
		and LATEST_EDITOR = #{latestEditor,jdbcType=INTEGER}
	
	
		and LATEST_EDIT_TIME = #{latestEditTime,jdbcType=BIGINT}
	
    
        and STATE = #{state,jdbcType=INTEGER},
    
    
        and LOCK_BY = #{lockBy,jdbcType=INTEGER},
    
    
        and LOCK_TIME = #{lockTime,jdbcType=BIGINT},
    
    
        and PWD = #{pwd,jdbcType=VARCHAR}
    
    
        and CONTENT = #{content,jdbcType=LONGVARCHAR}
    
    order by type desc,name asc

    select 
    	doc.ID, NAME, TYPE, PATH, PID, VID, PWD, CREATOR, CREATE_TIME, STATE
    from doc
    LEFT JOIN doc_auth on doc.ID = doc_auth.DOC_ID and doc_auth.USER_ID = #{userId,jdbcType=INTEGER}
    where doc_auth.ACCESS = 1
    
    	and doc.ID = #{docId,jdbcType=INTEGER}
    
    
    	and doc.PID = #{pDocId,jdbcType=INTEGER}
    
    
		and doc.VID = #{reposId,jdbcType=INTEGER}

    select 
    	doc.ID, NAME, TYPE, PATH, PID, VID, PWD, CREATOR, CREATE_TIME, STATE
    from doc
    LEFT JOIN doc_auth on doc.ID = doc_auth.DOC_ID and  doc_auth.USER_ID = #{userId,jdbcType=INTEGER} 
    where (doc_auth.ACCESS = 1 or doc_auth.ACCESS is null)
    
    	and doc.ID = #{docId,jdbcType=INTEGER}
    
    
    	and doc.PID = #{pDocId,jdbcType=INTEGER}
    
    
		and doc.VID = #{reposId,jdbcType=INTEGER}

	select 
    
    from doc
	where 1 = 1 
	
		and (
			NAME like '%${name}%'
			
				or ID in (${ids})
			 
			)
	
	
		and VID = #{reposId}
	
	
		and PID = #{pDocId}
