	  	limit ${start},${number}

select doc_auth.ID as id, REPOS_ID as reposId, IS_ADMIN as isAdmin, ACCESS, EDIT_EN as editEn, ADD_EN as addEn, DELETE_EN as deleteEn, HERITABLE, USER_ID as userId, user.NAME as userName, GROUP_ID as groupId, user_group.NAME as groupName, DOC_ID as docId, doc.NAME as docName, doc.PATH as docPath from doc_auth LEFT JOIN user on doc_auth.user_id = user.id LEFT JOIN user_group on doc_auth.group_id = user_group.id LEFT JOIN doc on doc_auth.doc_id = doc.id WHERE 1=1
and doc_auth.REPOS_ID = #{reposId,jdbcType=INTEGER}
and doc_auth.DOC_ID = #{docId,jdbcType=INTEGER}
and doc_auth.USER_ID = #{userId,jdbcType=INTEGER}
and doc_auth.GROUP_ID = #{groupId,jdbcType=INTEGER}
